tinymce.PluginManager.add("modern_footnotes",function(n,o){n.addButton("modern_footnotes",{title:"Add a Footnote",icon:"modern-footnotes-admin-button",onclick:function(){n.focus();var o=n.selection.getContent();o.length>0?-1==o.indexOf("[modern_footnote]")&&-1==o.indexOf("[/modern_footnote]")&&-1==o.indexOf("[mfn]")&&-1==o.indexOf("[/mfn]")?n.selection.setContent("[mfn]"+o+"[/mfn]"):-1!=o.indexOf("[modern_footnote]")&&-1!=o.indexOf("[/modern_footnote]")?n.selection.setContent(o.replace(/\[modern\_footnote\]/,"").replace(/\[\/modern\_footnote\]/,"")):-1!=o.indexOf("[mfn]")&&-1!=o.indexOf("[/mfn]")&&n.selection.setContent(o.replace(/\[mfn\]/,"").replace(/\[\/mfn\]/,"")):n.windowManager.open({title:"Insert Footnote",body:[{type:"textbox",name:"footnote",label:"Footnote"}],onsubmit:function(o){n.insertContent("[mfn]"+o.data.footnote+"[/mfn]")}})}})});