tinymce.PluginManager.add("modern_footnotes",function(o,n){o.addButton("modern_footnotes",{title:"Add a Footnote",icon:"modern-footnotes-admin-button",onclick:function(){o.focus();var n=o.selection.getContent();n.length>0?-1==n.indexOf("[modern_footnote]")&&-1==n.indexOf("[/modern_footnote]")&&-1==n.indexOf("[mfn]")&&-1==n.indexOf("[/mfn]")?o.selection.setContent("[modern_footnote]"+n+"[/modern_footnote]"):-1!=n.indexOf("[modern_footnote]")&&-1!=n.indexOf("[/modern_footnote]")?o.selection.setContent(n.replace(/\[modern\_footnote\]/,"").replace(/\[\/modern\_footnote\]/,"")):-1!=n.indexOf("[mfn]")&&-1!=n.indexOf("[/mfn]")&&o.selection.setContent(n.replace(/\[mfn\]/,"").replace(/\[\/mfn\]/,"")):o.windowManager.open({title:"Insert Footnote",body:[{type:"textbox",name:"footnote",label:"Footnote"}],onsubmit:function(n){o.insertContent("[mfn]"+n.data.footnote+"[/mfn]")}})}})});